using Microsoft.CodeAnalysis;
using Microsoft.CodeAnalysis.CSharp.Syntax;
using System.Text;

namespace RidgeSourceGenerator;

public static class SourceGenerationHelper
{
    private const string Header = @"//------------------------------------------------------------------------------
// <auto-generated>
//     This code was generated by the Ridge source generator
//
//     Changes to this file may cause incorrect behavior and will be lost if
//     the code is regenerated.
// </auto-generated>
//------------------------------------------------------------------------------
";

    private static int _counter;

    public static string GenerateExtensionClass(
        StringBuilder sb,
        ControllerToGenerate controllerToGenerate)
    {
        sb.Append($"// Counter: {Interlocked.Increment(ref _counter)}");

        var className = $"{controllerToGenerate.Name}Caller";
        sb.Append(Header);
        sb.Append(@"
#nullable enable

using Ridge.CallResult.Controller;
using Ridge.Interceptor;
using Ridge.Interceptor.ActionInfo;
using Ridge.Interceptor.InterceptorFactory;
using Ridge.LogWriter;
using Ridge.Pipeline.Public;
using Ridge.Serialization;
using Ridge.Transformers;
using System;
using System.Collections.Generic;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Reflection;
using System.Threading.Tasks;

");
        if (!string.IsNullOrEmpty(controllerToGenerate.Namespace))
        {
            sb.Append(@"namespace ").AppendLine(controllerToGenerate.Namespace);
            sb.AppendLine("{");
        }

        sb.Append(@"
/// <summary>
/// Class generated using source generator to make strongly typed calls in tests.
/// </summary>
public class ");
        sb.Append(className);
        sb.Append(@"
{
    private readonly HttpClient _httpClient;
    private readonly IServiceProvider _serviceProvider;
    private readonly ILogWriter? _logWriter;
    private readonly IRidgeSerializer? _ridgeSerializer;
    private readonly RequestBuilder _requestBuilder = new();

    /// <summary>
    ///     Create controller factory.
    /// </summary>
    /// <param name=""httpClient""></param>
    /// <param name=""serviceProvider"">Service provider present in WebApplicationFactory.</param>
    /// <param name=""logWriter"">
    ///     Used to log requests and responses from server.
    ///     Use <see cref=""XunitLogWriter"" /> or <see cref=""NunitLogWriter"" /> or <see cref=""NunitProgressLogWriter""/> or implement custom <see cref=""ILogWriter"" />
    /// </param>
    /// <param name=""ridgeSerializer"">
    ///     Serializer used to serialize and deserialize requests.
    ///     Serializer is by default chosen based on asp.net settings. If you need custom serializer implement
    ///     <see cref=""IRidgeSerializer"" />.
     /// </param>
    public ");
        sb.Append(className);
        sb.AppendLine(@"(
        HttpClient httpClient,
        IServiceProvider serviceProvider,
        ILogWriter? logWriter = null,
        IRidgeSerializer? ridgeSerializer = null)
    {
        _httpClient = httpClient;
        _serviceProvider = serviceProvider;
        _logWriter = logWriter;
        _ridgeSerializer = ridgeSerializer;
    }

    /// <summary>
    ///     Adds <see cref=""IHttpRequestPipelinePart""/> which can transform request after url is constructed.
    /// </summary>
    /// <param name=""httpRequestPipelinePart""></param>
    public void AddHttpRequestPipelinePart(
        IHttpRequestPipelinePart httpRequestPipelinePart)
    {
        _requestBuilder.AddHttpRequestPipelinePart(httpRequestPipelinePart);
    }

    /// <summary>
    ///     Adds <see cref=""IActionInfoTransformer""/> which can transform request before url is constructed.
    /// </summary>
    /// <param name=""actionInfoTransformer""></param>
    public void AddActionInfoTransformer(
        IActionInfoTransformer actionInfoTransformer)
    {
        _requestBuilder.AddActionInfoTransformer(actionInfoTransformer);
    }

    /// <summary>
    ///     Adds <see cref=""IActionInfoTransformer""/> which adds header to the request.
    /// </summary>
    /// <param name=""key""></param>
    /// <param name=""value""></param>
    public void AddHeader(
        string key,
        string? value)
    {
        _requestBuilder.AddHeader(key, value);
    }

    /// <summary>
    ///     Adds multiple <see cref=""IActionInfoTransformer""/> which adds headers to the request.
    /// </summary>
    /// <param name=""headers""></param>
    public void AddHeaders(
        IDictionary<string, string?> headers)
    {
        _requestBuilder.AddHeaders(headers);
    }

    /// <summary>
    ///     Adds pipeline part which sets Authorization.
    /// </summary>
    /// <param name=""authenticationHeaderValue""></param>
    public void AddAuthorization(
        AuthenticationHeaderValue authenticationHeaderValue)
    {
        _requestBuilder.AddAuthorization(authenticationHeaderValue);
    }
    ");


        foreach (var publicMethod in controllerToGenerate.PublicMethods)
        {
            if (publicMethod.IsGenericMethod)
            {
                continue;
            }

            var syntax = ((MethodDeclarationSyntax?)publicMethod
               .DeclaringSyntaxReferences.FirstOrDefault()
              ?.GetSyntax());

            // action must be explicitly defined
            if (syntax == null)
            {
                continue;
            }

            string? returnType = publicMethod.ReturnType.Name;

            if (publicMethod.ReturnType is not INamedTypeSymbol fullReturnType)
            {
                continue;
            }

            returnType = GetActualReturnType(fullReturnType);

            sb.Append(@"    public async ");

            if (returnType == null)
            {
                sb.Append("Task<ControllerCallResult>");
            }
            else
            {
                sb.Append($"Task<ControllerCallResult<{returnType}>>");
            }

            sb.Append(" ");
            sb.Append("Call_");
            sb.Append(publicMethod.Name);
            sb.Append("(");
            var discriminator = "";
            foreach (var publicMethodParameter in publicMethod.Parameters)
            {
                sb.Append(discriminator);
                sb.Append(@"
            ");

                sb.Append(publicMethodParameter.Type);
                sb.Append(" ");
                sb.Append(publicMethodParameter.Name);
                discriminator = @",";
            }

            sb.AppendLine(")");

            sb.AppendLine(@"    {");
            sb.Append(@"        var methodName = ");
            sb.Append("nameof(");
            sb.Append(controllerToGenerate.FullyQualifiedName);
            sb.Append(".");
            sb.Append(publicMethod.Name);
            sb.AppendLine(");");

            sb.Append(@"        var controllerType = typeof(");
            sb.Append(controllerToGenerate.FullyQualifiedName);
            sb.AppendLine(");");

            sb.AppendLine(@"        var arguments = new List<object?>()");
            sb.AppendLine(@"        {");
            foreach (var publicMethodParameter in publicMethod.Parameters)
            {
                sb.Append(@"            ");
                sb.Append(publicMethodParameter.Name);
                sb.AppendLine(",");
            }

            sb.AppendLine(@"        };");


            sb.AppendLine(@"
        var caller = new ControllerCaller(_requestBuilder,
            _logWriter,
            _httpClient,
            _serviceProvider,
            _ridgeSerializer);");


            sb.AppendLine(@"
        var methodInfo = controllerType.GetMethod(methodName, new Type[] {");

            foreach (var publicMethodParameter in publicMethod.Parameters)
            {
                sb.Append(@"
            ");
                sb.Append("typeof(");
                sb.Append(publicMethodParameter.Type.ToDisplayString(NullableFlowState.None));
                sb.Append("),");
            }

            sb.AppendLine(@"
        });");
            sb.AppendLine(@"
        if (methodInfo == null)
        {
            throw new InvalidOperationException($""Method with name {methodName} not found in class {controllerType.FullName}."");
        }
        ");

            if (returnType == null)
            {
                sb.AppendLine(@"        return await caller.CallAction(arguments, methodInfo);");
            }
            else
            {
                sb.AppendLine($@"        return await caller.CallAction<{returnType}>(arguments, methodInfo);");
            }

            sb.AppendLine(@"    }");
            sb.AppendLine();
        }

        sb.AppendLine("}");

        if (!string.IsNullOrEmpty(controllerToGenerate.Namespace))
        {
            sb.AppendLine("}");
        }


        return sb.ToString();
    }

    // TODO co kdyz dedim z IActionResult??
    private static string? GetActualReturnType(
        INamedTypeSymbol returnType)
    {
        if (returnType.Arity == 0)
        {
            if (returnType.Name == "Task")
            {
                return null;
            }

            if (returnType.Name == "Void")
            {
                return null;
            }

            if (returnType.Name == "IActionResult")
            {
                return null;
            }

            return returnType.ToString();
        }

        if (returnType.Arity == 1)
        {
            var firstGenericArgument = (INamedTypeSymbol)returnType.TypeArguments.First();

            //Task<XXX>
            if (returnType.Name == "Task")
            {
                //Task<IActionResult>
                if (firstGenericArgument.Name == "IActionResult")
                {
                    return null;
                }
                //Task<ActionResult<XXX>> or Task<ActionResult> 

                if (firstGenericArgument.Name == "ActionResult")
                {
                    if (firstGenericArgument.Arity == 0)
                    {
                        //Task<ActionResult>
                        return null;
                    }

                    //Task<ActionResult<XXX>>
                    return firstGenericArgument.TypeArguments.First().ToString();
                }
                //Task<XXX>

                return firstGenericArgument.ToString();
            }
            //ActionResult<XXX> or ActionResult 

            if (returnType.Name == "ActionResult")
            {
                if (returnType.Arity == 0)
                {
                    //ActionResult
                    return null;
                }

                //ActionResult<XXX>
                return returnType.TypeArguments.First().ToString();
            }
            // Generic<XXX>

            return returnType.ToString();
        }
        // Generic<XXX>

        return returnType.ToString();
    }
}
